            this.Write((Model.IsFlags ? "FormatFlagJsonStringsLength" : "GetJsonStringLengthInlined"));
            this.Write("((");
            this.Write((Model.UnderlyingType));
            this.Write(")value);\n    }\n\n");

            WriteFormatString(x => x.ResolvedJsonValue, "JsonString");
        }

        private void WriteMatchBlock()
        {
            if (Model.Values.IsEmpty || Model.IsFlags)
            {
                return;
            }

            this.Write("\n    /// <summary>\n    /// Provides pattern matching functionality for the <see cref=\"");
            this.Write((Model.RefName));
            this.Write(
                "\"/> enum by returning the corresponding value based on the enum value.\n    /// </summary>\n    /// <typeparam name=\"TResult\">The type of the result to return for each member match.</typeparam>\n    /// <param name=\"value\">The <see cref=\""
            );
            this.Write((Model.RefName));
            this.Write("\"/> enum value to match against.</param>\n");

            foreach (var curr in Model.UniqueValues)
            {
                this.Write("    /// <param name=\"");
                this.Write((curr.MemberName));
                this.Write("\">The value to return when the enum value is ");
                this.Write((curr.MemberName));
                this.Write(".</param>\n");
            }

            this.Write(
                "    /// <returns>The corresponding result value based on the enum value.</returns>\n    /// <exception cref=\"ArgumentOutOfRangeException\">Thrown when the enum value does not match any of the expected member values.</exception>\n    public static TResult Match<TResult>(\n        this "
            );
            this.Write((Model.RefName));
            this.Write(" value");

            foreach (var curr in Model.UniqueValues)
            {
                this.Write(",\n        TResult ");
                this.Write((curr.MemberName));
            }

            this.Write(")\n    {\n        return (");
            this.Write((Model.UnderlyingType));
            this.Write(")value switch\n        {\n");

            foreach (var curr in Model.UniqueValues)
            {
                this.Write("            ");
                this.Write((curr.MemberValue));
                this.Write(" => ");
                this.Write((curr.MemberName));
                this.Write(",\n");
            }

            this.Write(
                "            _ => throw new ArgumentOutOfRangeException(nameof(value), value, null)\n        };\n    }\n\n    /// <summary>\n    /// Provides pattern matching functionality for the <see cref=\""
            );
            this.Write((Model.RefName));
            this.Write(
                "\"/> enum by executing the corresponding function based on the enum value.\n    /// </summary>\n    /// <typeparam name=\"TResult\">The type of the result to return from the executed function.</typeparam>\n    /// <param name=\"value\">The <see cref=\""
            );
            this.Write((Model.RefName));
            this.Write("\"/> enum value to match against.</param>\n");

            foreach (var curr in Model.UniqueValues)
            {
                this.Write("    /// <param name=\"");
                this.Write((curr.MemberName));
                this.Write("\">The function to execute when the enum value is ");
                this.Write((curr.MemberName));
                this.Write(".</param>\n");
            }

            this.Write(
                "    /// <returns>The result of executing the corresponding function based on the enum value.</returns>\n    /// <exception cref=\"ArgumentOutOfRangeException\">Thrown when the enum value does not match any of the expected <see cref=\""
            );
            this.Write((Model.RefName));
            this.Write("\"/> values.</exception>\n    public static TResult Match<TResult>(\n        this ");
            this.Write((Model.RefName));
            this.Write(" value");

            foreach (var curr in Model.UniqueValues)
            {
                this.Write(",\n        Func<");
                this.Write((Model.RefName));
                this.Write(", TResult> ");
                this.Write((curr.MemberName));
            }

            this.Write(")\n    {\n        return (");
            this.Write((Model.UnderlyingType));
            this.Write(")value switch\n        {\n");

            foreach (var curr in Model.UniqueValues)
            {
                this.Write("            ");
                this.Write((curr.MemberValue));
                this.Write(" => ");
                this.Write((curr.MemberName));
                this.Write("((");
                this.Write((Model.RefName));
                this.Write(")value),\n");
            }

            this.Write(
                "            _ => throw new ArgumentOutOfRangeException(nameof(value), value, null)\n        };\n    }\n"
            );
        }

        public EnumExtensionsWriter(StringBuilder builder)
            : base(builder) { }

        public override string GetFileName() => CodeWriterHelper.GetFileName(Model, "Extensions");

        protected override bool CanGenerateFor(EnumToGenerate model) =>
            (model.SelectedGenerators & (SelectedGenerators.MainGenerator | SelectedGenerators.JsonConverter)) != 0;
    }
}
